using UnityEngine;using UnityEngine.UI;public class MenuButtonsScript : MonoBehaviour{    [SerializeField]private Pause pause;    public void ChangeScene(string sceneName)    {        Application.LoadLevel(sceneName);     }        public void ExitButtom()    {        Application.Quit();     }        public void Continue()    {        pause.ispuse = false;    }        public void StopMenuMusic()    {        //MakeIt("Volume", "NotVolume");        GameObject.Find("MenuMusic").GetComponent<MenuMusic>().StopMusic();    }        public void PlayMenuMusic()    {        //MakeIt("NotVolume", "Volume");        GameObject.Find("MenuMusic").GetComponent<MenuMusic>().PlayMusic();    }    private void MakeIt(string obj1, string obj2)    {        var volume = GameObject.Find(obj1);        var notVolume = GameObject.Find(obj2);        volume.GetComponent<BoxCollider2D>().enabled = false;        volume.GetComponent<Button>().enabled = false;        volume.GetComponent<Image>().color = Color.clear;        notVolume.GetComponent<BoxCollider2D>().enabled = true;        notVolume.GetComponent<Button>().enabled = true;        notVolume.GetComponent<Image>().color = Color.white;    }}